function init_carusel(){$("#carousel1").CloudCarousel({reflHeight:76,reflGap:1,xPos:document.body.clientWidth/2,yPos:document.body.clientHeight/2-230,yRadius:240,speed:.15,reflOpacity:.5,mouseWheel:!0})}function search(){var str=document.getElementById("search_text").value,carusel=document.getElementById("carousel1"),result=document.getElementById("result"),id_page,j=0;carusel.style.width=document.body.clientWidth+"px",carusel.style.height=document.body.clientHeight+"px",str1!=str&&(carusel.style.display="none",$("#spinner").show(),$.ajax({type:"POST",url:"result.json",data:"search="+str,success:function(msg){str1=str;var data=eval(msg);carusel.innerHTML="";for(var i=0;i<data.length;i+=2)id_page=Math.random()*5,massiv[j]=i+1,carusel.innerHTML+="<img class = 'cloudcarousel' src='"+data[i]+"' onClick='unvisible(\""+data[i+1]+'",'+(i+1)+")'/>",$("#"+(i+1)).load("http://"+data[i+1]),j++;init_carusel()}}))}function unvisible(e,t){document.getElementById("carousel1").style.visibility="hidden",document.getElementById("theSearch").style.visibility="hidden",res=document.getElementById(""+t),res.style.display="block"}function back(){res.style.display="none",document.getElementById("carousel1").style.visibility="visible",document.getElementById("theSearch").style.visibility="visible"}$(document).ready(function(){$(document).keydown(function(e){e.keyCode==13&&search()})});var str1,result,res,massiv=new Array;