function init_carusel(){$("#carousel1").CloudCarousel({reflHeight:76,reflGap:1,xPos:document.body.clientWidth/2,yPos:document.body.clientHeight/2-230,yRadius:240,speed:.15,reflOpacity:.5,mouseWheel:!0}),document.bgColor="black"}function search(){var str=document.getElementById("search_text").value,carusel=document.getElementById("carousel1"),result=document.getElementById("result"),id_page,j=0;carusel.style.width=document.body.clientWidth+"px",carusel.style.height=document.body.clientHeight+"px",str1!=str&&(carusel.style.display="none",$("#spinner").show(),$.ajax({type:"POST",url:"result.json",data:"search="+str,success:function(msg){str1=str;var data=eval(msg);carusel.innerHTML="";for(var i=0;i<data.length;i+=2)id_page=Math.random()*5,massiv[j]=id_page,carusel.innerHTML+="<img class = 'cloudcarousel' src='"+data[i]+"' onClick='unvisible(\""+data[i+1]+'",'+id_page+")'/>",result.innerHTML+="<iframe id='"+id_page+"' src=\"http://"+data[i+1]+'" width="630px" height=\'300px\' class=\'l-projects_shadow\' style="visibility:hidden;z-index:10000;">Ваш браузер не поддерживает iframe</iframe>',j++;init_carusel()}}))}function unvisible(e,t){var n=document.getElementById(t).style.top,r=0+n;document.getElementById("result").style.display="block";for(var i=0;i<massiv.length;i++)massiv[i]!=t&&(document.getElementById(massiv[i]).style.display="none");document.getElementById(t).style.top=-r-300,document.getElementById(t).style.visibility="visible",document.getElementById("carousel1").style.visibility="hidden",document.getElementById("theSearch").style.visibility="hidden"}$(document).ready(function(){$(document).keydown(function(e){e.keyCode==13&&search()})});var str1,result,massiv=new Array;